sourcesContent:
import React, { useState, useRef } from 'react';
import { Calendar, Upload, Copy, Share, Target, FileText, ChefHat, Dumbbell, Camera, Lightbulb, X, Eye, Edit2, Check } from 'lucide-react';

function SocialMediaAgent() {
  // State initialization
  const [recipes, setRecipes] = useState([]);
  const [workouts, setWorkouts] = useState([]);
  const [realEstateTips, setRealEstateTips] = useState([]);
  const [mindfulnessPosts, setMindfulnessPosts] = useState([]);
  const [contentCalendar, setContentCalendar] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');

  // Form states
  const [newRecipe, setNewRecipe] = useState({ title: '', ingredients: '', instructions: '', tags: '' });
  const [newWorkout, setNewWorkout] = useState({ title: '', exercises: '', duration: '', difficulty: '', tags: '' });
  const [newRealEstateTip, setNewRealEstateTip] = useState({ title: '', content: '', category: '', tags: '' });
  const [newMindfulness, setNewMindfulness] = useState({ title: '', content: '', practice: '', tags: '' });

  // Preview and editing states
  const [activeImageModal, setActiveImageModal] = useState(null);
  const [previewRecipe, setPreviewRecipe] = useState(null);
  const [previewWorkout, setPreviewWorkout] = useState(null);
  const [editingRecipe, setEditingRecipe] = useState(null);
  const [editingWorkout, setEditingWorkout] = useState(null);

  // AI settings
  const [apiKey, setApiKey] = useState('');
  const [llmProvider, setLlmProvider] = useState('openai');
  const [isGenerating, setIsGenerating] = useState(false);
  const [showApiSettings, setShowApiSettings] = useState(false);
  const [contentMix, setContentMix] = useState({
    recipes: true,
    workouts: true,
    realEstate: true,
    mindfulness: true,
    motivational: true,
    educational: true
  });
  const [weeklySchedule, setWeeklySchedule] = useState({
    monday: 'motivational',
    tuesday: 'workout',
    wednesday: 'mindfulness',
    thursday: 'realEstate',
    friday: 'educational',
    saturday: 'recipe',
    sunday: 'random'
  });

  // Refs
  const recipeFileInputRef = useRef(null);
  const workoutFileInputRef = useRef(null);

  const platforms = {
    instagram: { name: 'Instagram', color: 'bg-pink-500', icon: 'ðŸ“¸' },
    linkedin: { name: 'LinkedIn', color: 'bg-blue-600', icon: 'ðŸ’¼' },
    facebook: { name: 'Facebook', color: 'bg-blue-500', icon: 'ðŸ‘¥' }
  };

  const contentTypes = {
    recipe: { name: 'Recipe', icon: ChefHat, color: 'text-orange-500' },
    workout: { name: 'Workout', icon: Dumbbell, color: 'text-green-500' },
    realEstate: { name: 'Real Estate', icon: FileText, color: 'text-blue-500' },
    mindfulness: { name: 'Mindfulness', icon: Target, color: 'text-purple-500' },
    motivational: { name: 'Motivational', icon: Target, color: 'text-pink-500' },
    educational: { name: 'Educational', icon: FileText, color: 'text-indigo-500' }
  };

  // Helper functions
  const addRecipe = () => {
    if (newRecipe.title.trim()) {
      setRecipes([...recipes, { ...newRecipe, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewRecipe({ title: '', ingredients: '', instructions: '', tags: '' });
    }
  };

  const addWorkout = () => {
    if (newWorkout.title.trim()) {
      setWorkouts([...workouts, { ...newWorkout, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewWorkout({ title: '', exercises: '', duration: '', difficulty: '', tags: '' });
    }
  };

  const addRealEstateTip = () => {
    if (newRealEstateTip.title.trim()) {
      setRealEstateTips([...realEstateTips, { ...newRealEstateTip, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewRealEstateTip({ title: '', content: '', category: '', tags: '' });
    }
  };

  const addMindfulness = () => {
    if (newMindfulness.title.trim()) {
      setMindfulnessPosts([...mindfulnessPosts, { ...newMindfulness, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewMindfulness({ title: '', content: '', practice: '', tags: '' });
    }
  };

  const deleteRecipe = (id) => {
    setRecipes(recipes.filter(r => r.id !== id));
    if (previewRecipe?.id === id) setPreviewRecipe(null);
  };

  const deleteWorkout = (id) => {
    setWorkouts(workouts.filter(w => w.id !== id));
    if (previewWorkout?.id === id) setPreviewWorkout(null);
  };

  const deleteRealEstate = (id) => {
    setRealEstateTips(realEstateTips.filter(t => t.id !== id));
  };

  const deleteMindfulness = (id) => {
    setMindfulnessPosts(mindfulnessPosts.filter(m => m.id !== id));
  };

  const generatePostVariations = (content, type) => {
    const variations = {
      instagram: {
        recipe: `ðŸ½ï¸ ${content.title}\n\nâœ¨ Perfect for your weekly meal prep!\n\n#healthyeating #mealprep #recipe`,
        workout: `ðŸ’ª ${content.title}\n\nðŸ”¥ ${content.duration || 'Get moving'} of pure energy!\n\n#fitness #workout #motivation`,
        realEstate: `ðŸ¡ ${content.title}\n\nâœ¨ Your dream home awaits!\n\n#realestate #homebuying #property`,
        mindfulness: `ðŸ§˜ ${content.title}\n\nâœ¨ Find your peace today\n\n#mindfulness #meditation #wellness`,
        motivational: `âœ¨ Monday Motivation âœ¨\n\n"Your only limit is your mind"\n\n#motivation #mindset`,
        educational: `ðŸ“š Did you know?\n\n${content.title}\n\n#facts #education #wellness`
      },
      linkedin: {
        recipe: `Nutrition and Performance: ${content.title}\n\n#nutrition #wellness #productivity`,
        workout: `Peak Performance Through Fitness: ${content.title}\n\n#leadership #wellness #fitness`,
        realEstate: `Market Insight: ${content.title}\n\nNavigating today's real estate landscape.\n\n#realestate #investing #markettrends`,
        mindfulness: `Leadership Through Mindfulness: ${content.title}\n\nMental clarity drives success.\n\n#leadership #mindfulness #productivity`,
        motivational: `Leadership Insight: ${content.title}\n\n#leadership #mindset #success`,
        educational: `Industry Insight: ${content.title}\n\n#industry #insights #knowledge`
      },
      facebook: {
        recipe: `ðŸ¥— Family Recipe: ${content.title}\n\n#familytime #healthycooking #recipes`,
        workout: `ðŸ’ª Community Fitness: ${content.title}\n\n#communityfitness #workout #motivation`,
        realEstate: `ðŸ¡ Home Sweet Home: ${content.title}\n\nLet me help you find your perfect place!\n\n#realestate #homeowner`,
        mindfulness: `ðŸŒ¸ Wellness Wednesday: ${content.title}\n\nTake a moment for yourself.\n\n#mindfulness #selfcare`,
        motivational: `âœ¨ ${content.title}\n\n#motivation #community #support`,
        educational: `ðŸ’¡ ${content.title}\n\n#wellness #health #education`
      }
    };

    return variations;
  };

  const generateWeeklyContent = () => {
    setIsGenerating(true);
    const weeklyPlan = [];
    const today = new Date();
    const dayNames = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];

    const availableContent = {
      recipe: recipes,
      workout: workouts,
      realEstate: realEstateTips,
      mindfulness: mindfulnessPosts
    };

    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      const dayName = dayNames[date.getDay()];

      let contentType = weeklySchedule[dayName];
      let content;

      // Handle 'random' selection
      if (contentType === 'random') {
        const enabledTypes = Object.keys(contentMix).filter(key => 
          contentMix[key] && (availableContent[key]?.length > 0 || ['motivational', 'educational'].includes(key))
        );
        if (enabledTypes.length > 0) {
          contentType = enabledTypes[Math.floor(Math.random() * enabledTypes.length)];
        } else {
          contentType = 'motivational';
        }
      }

      // Get content based on type
      if (contentType === 'recipe' && availableContent.recipe.length > 0) {
        content = availableContent.recipe[Math.floor(Math.random() * availableContent.recipe.length)];
      } else if (contentType === 'workout' && availableContent.workout.length > 0) {
        content = availableContent.workout[Math.floor(Math.random() * availableContent.workout.length)];
      } else if (contentType === 'realEstate' && availableContent.realEstate.length > 0) {
        content = availableContent.realEstate[Math.floor(Math.random() * availableContent.realEstate.length)];
      } else if (contentType === 'mindfulness' && availableContent.mindfulness.length > 0) {
        content = availableContent.mindfulness[Math.floor(Math.random() * availableContent.mindfulness.length)];
      } else if (contentType === 'motivational') {
        content = { title: 'Daily Motivation' };
      } else if (contentType === 'educational') {
        content = { title: 'Wellness Tips' };
      } else {
        // Fallback if selected type has no content
        content = { title: 'Daily Inspiration' };
        contentType = 'motivational';
      }

      const fallback = generatePostVariations(content, contentType);
      const postData = {
        id: Date.now() + i,
        date: date.toISOString().split('T')[0],
        dayName: dayName,
        contentType,
        content,
        platforms: ['instagram', 'linkedin', 'facebook'],
        status: 'draft',
        variations: {
          instagram: fallback.instagram[contentType] || fallback.instagram.motivational,
          linkedin: fallback.linkedin[contentType] || fallback.linkedin.motivational,
          facebook: fallback.facebook[contentType] || fallback.facebook.motivational
        }
      };

      weeklyPlan.push(postData);
    }

    setContentCalendar(weeklyPlan);
    setIsGenerating(false);
  };

  const copyToClipboard = (text, platform) => {
    navigator.clipboard.writeText(text);
    alert(`${platform} content copied!`);
  };

  return (
    <div className="max-w-7xl mx-auto p-6 bg-gray-50 min-h-screen">
      <input
        type="file"
        ref={recipeFileInputRef}
        accept=".json,.csv,.txt"
        style={{ display: 'none' }}
      />
      <input
        type="file"
        ref={workoutFileInputRef}
        accept=".json,.csv,.txt"
        style={{ display: 'none' }}
      />

      <div className="bg-white rounded-lg shadow-lg mb-6">
        <div className="p-6 border-b">
          <h1 className="text-3xl font-bold text-gray-800 flex items-center">
            <Share className="mr-3 text-blue-500" />
            Social Media Content Agent
          </h1>
          <p className="text-gray-600 mt-2">AI-powered content planning for Instagram, LinkedIn, and Facebook</p>
        </div>

        <div className="flex border-b overflow-x-auto">
          {['dashboard', 'recipes', 'workouts', 'realestate', 'mindfulness', 'calendar', 'generate'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`px-6 py-3 font-medium capitalize whitespace-nowrap ${
                activeTab === tab 
                  ? 'border-b-2 border-blue-500 text-blue-600 bg-blue-50' 
                  : 'text-gray-600 hover:text-gray-800 hover:bg-gray-50'
              }`}
            >
              {tab === 'realestate' ? 'Real Estate' : tab}
            </button>
          ))}
        </div>
      </div>

      {activeTab === 'dashboard' && (
        <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-5 gap-6">
          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Recipes</p>
                <p className="text-2xl font-bold text-orange-500">{recipes.length}</p>
              </div>
              <ChefHat className="text-orange-500" size={32} />
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Workouts</p>
                <p className="text-2xl font-bold text-green-500">{workouts.length}</p>
              </div>
              <Dumbbell className="text-green-500" size={32} />
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Real Estate</p>
                <p className="text-2xl font-bold text-blue-600">{realEstateTips.length}</p>
              </div>
              <FileText className="text-blue-600" size={32} />
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Mindfulness</p>
                <p className="text-2xl font-bold text-purple-500">{mindfulnessPosts.length}</p>
              </div>
              <Target className="text-purple-500" size={32} />
            </div>
          </div>

          <div className="bg-white p-6 rounded-lg shadow">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-gray-600 text-sm">Posts</p>
                <p className="text-2xl font-bold text-blue-500">{contentCalendar.length}</p>
              </div>
              <Calendar className="text-blue-500" size={32} />
            </div>
          </div>
        </div>
      )}

      {activeTab === 'recipes' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Recipe Management</h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Recipe title"
              value={newRecipe.title}
              onChange={(e) => setNewRecipe({...newRecipe, title: e.target.value})}
              className="w-full p-3 border rounded-lg"
            />
            <textarea
              placeholder="Ingredients"
              value={newRecipe.ingredients}
              onChange={(e) => setNewRecipe({...newRecipe, ingredients: e.target.value})}
              className="w-full p-3 border rounded-lg h-24"
            />
            <button
              onClick={addRecipe}
              className="px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600"
            >
              Add Recipe
            </button>
          </div>
          <div className="mt-6 space-y-2">
            {recipes.map((recipe) => (
              <div key={recipe.id} className="p-3 border rounded flex justify-between items-center">
                <span>{recipe.title}</span>
                <button onClick={() => deleteRecipe(recipe.id)} className="text-red-500">
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'workouts' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Workout Management</h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Workout title"
              value={newWorkout.title}
              onChange={(e) => setNewWorkout({...newWorkout, title: e.target.value})}
              className="w-full p-3 border rounded-lg"
            />
            <textarea
              placeholder="Exercises"
              value={newWorkout.exercises}
              onChange={(e) => setNewWorkout({...newWorkout, exercises: e.target.value})}
              className="w-full p-3 border rounded-lg h-24"
            />
            <input
              type="text"
              placeholder="Duration"
              value={newWorkout.duration}
              onChange={(e) => setNewWorkout({...newWorkout, duration: e.target.value})}
              className="w-full p-3 border rounded-lg"
            />
            <button
              onClick={addWorkout}
              className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600"
            >
              Add Workout
            </button>
          </div>
          <div className="mt-6 space-y-2">
            {workouts.map((workout) => (
              <div key={workout.id} className="p-3 border rounded flex justify-between items-center">
                <span>{workout.title}</span>
                <button onClick={() => deleteWorkout(workout.id)} className="text-red-500">
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'realestate' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Real Estate Content</h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Tip title"
              value={newRealEstateTip.title}
              onChange={(e) => setNewRealEstateTip({...newRealEstateTip, title: e.target.value})}
              className="w-full p-3 border rounded-lg"
            />
            <textarea
              placeholder="Content"
              value={newRealEstateTip.content}
              onChange={(e) => setNewRealEstateTip({...newRealEstateTip, content: e.target.value})}
              className="w-full p-3 border rounded-lg h-24"
            />
            <select
              value={newRealEstateTip.category}
              onChange={(e) => setNewRealEstateTip({...newRealEstateTip, category: e.target.value})}
              className="w-full p-3 border rounded-lg"
            >
              <option value="">Select category</option>
              <option value="Buying">Home Buying</option>
              <option value="Selling">Home Selling</option>
              <option value="Market">Market Updates</option>
            </select>
            <button
              onClick={addRealEstateTip}
              className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
            >
              Add Real Estate Tip
            </button>
          </div>
          <div className="mt-6 space-y-2">
            {realEstateTips.map((tip) => (
              <div key={tip.id} className="p-3 border rounded flex justify-between items-center">
                <span>{tip.title}</span>
                <button onClick={() => deleteRealEstate(tip.id)} className="text-red-500">
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'mindfulness' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-4">Mindfulness Content</h2>
          <div className="space-y-3">
            <input
              type="text"
              placeholder="Post title"
              value={newMindfulness.title}
              onChange={(e) => setNewMindfulness({...newMindfulness, title: e.target.value})}
              className="w-full p-3 border rounded-lg"
            />
            <textarea
              placeholder="Content"
              value={newMindfulness.content}
              onChange={(e) => setNewMindfulness({...newMindfulness, content: e.target.value})}
              className="w-full p-3 border rounded-lg h-24"
            />
            <select
              value={newMindfulness.practice}
              onChange={(e) => setNewMindfulness({...newMindfulness, practice: e.target.value})}
              className="w-full p-3 border rounded-lg"
            >
              <option value="">Select practice</option>
              <option value="Meditation">Meditation</option>
              <option value="Breathing">Breathing Exercise</option>
              <option value="Gratitude">Gratitude Practice</option>
            </select>
            <button
              onClick={addMindfulness}
              className="px-4 py-2 bg-purple-500 text-white rounded-lg hover:bg-purple-600"
            >
              Add Mindfulness Post
            </button>
          </div>
          <div className="mt-6 space-y-2">
            {mindfulnessPosts.map((post) => (
              <div key={post.id} className="p-3 border rounded flex justify-between items-center">
                <span>{post.title}</span>
                <button onClick={() => deleteMindfulness(post.id)} className="text-red-500">
                  <X size={16} />
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'calendar' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-6">Content Calendar</h2>
          {contentCalendar.length === 0 ? (
            <p className="text-center text-gray-400 py-12">No content generated yet. Go to Generate tab!</p>
          ) : (
            <div className="space-y-4">
              {contentCalendar.map((post) => (
                <div key={post.id} className="border rounded-lg p-4">
                  <div className="flex justify-between items-start mb-3">
                    <div>
                      <div className="font-medium">{post.content.title}</div>
                      <div className="text-sm text-gray-500">
                        {post.date} â€¢ <span className="capitalize">{post.dayName}</span> â€¢ 
                        <span className={`ml-2 px-2 py-0.5 rounded text-xs ${
                          post.contentType === 'recipe' ? 'bg-orange-100 text-orange-800' :
                          post.contentType === 'workout' ? 'bg-green-100 text-green-800' :
                          post.contentType === 'realEstate' ? 'bg-blue-100 text-blue-800' :
                          post.contentType === 'mindfulness' ? 'bg-purple-100 text-purple-800' :
                          post.contentType === 'motivational' ? 'bg-pink-100 text-pink-800' :
                          'bg-indigo-100 text-indigo-800'
                        }`}>
                          {contentTypes[post.contentType]?.name || post.contentType}
                        </span>
                      </div>
                    </div>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-3">
                    {Object.entries(platforms).map(([platform, config]) => (
                      <div key={platform} className="border rounded p-3">
                        <div className="flex justify-between items-center mb-2">
                          <span className="font-medium text-sm">{config.icon} {config.name}</span>
                          <button
                            onClick={() => copyToClipboard(post.variations[platform], config.name)}
                            className="text-gray-400 hover:text-gray-600"
                          >
                            <Copy size={16} />
                          </button>
                        </div>
                        <div className="text-xs bg-gray-50 p-2 rounded max-h-32 overflow-y-auto">
                          {post.variations[platform]}
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {activeTab === 'generate' && (
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-semibold mb-6">Generate Weekly Content</h2>

          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-4">
            <h3 className="font-medium text-blue-800 mb-3">ðŸ“… Weekly Schedule</h3>
            <p className="text-sm text-blue-600 mb-3">Set what content type to post on each day:</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
              {['monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday', 'sunday'].map((day) => (
                <div key={day} className="flex items-center space-x-2">
                  <label className="text-sm font-medium capitalize w-24">{day}:</label>
                  <select
                    value={weeklySchedule[day]}
                    onChange={(e) => setWeeklySchedule({...weeklySchedule, [day]: e.target.value})}
                    className="flex-1 p-2 border rounded text-sm"
                  >
                    <option value="recipe">ðŸ³ Recipe</option>
                    <option value="workout">ðŸ’ª Workout</option>
                    <option value="realEstate">ðŸ¡ Real Estate</option>
                    <option value="mindfulness">ðŸ§˜ Mindfulness</option>
                    <option value="motivational">âœ¨ Motivational</option>
                    <option value="educational">ðŸ“š Educational</option>
                    <option value="random">ðŸŽ² Random Mix</option>
                  </select>
                </div>
              ))}
            </div>
            <div className="mt-3 p-3 bg-blue-100 rounded">
              <p className="text-xs text-blue-700">
                <strong>Tip:</strong> Select "Random Mix" for any day to automatically choose from available content. Make sure you have content added in each category you want to use!
              </p>
            </div>
          </div>

          <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-4">
            <h3 className="font-medium text-green-800 mb-3">ðŸ“Š Content Inventory</h3>
            <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.recipes}
                  onChange={(e) => setContentMix({...contentMix, recipes: e.target.checked})}
                />
                <span className="text-sm">ðŸ³ Recipes ({recipes.length})</span>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.workouts}
                  onChange={(e) => setContentMix({...contentMix, workouts: e.target.checked})}
                />
                <span className="text-sm">ðŸ’ª Workouts ({workouts.length})</span>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.realEstate}
                  onChange={(e) => setContentMix({...contentMix, realEstate: e.target.checked})}
                />
                <span className="text-sm">ðŸ¡ Real Estate ({realEstateTips.length})</span>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.mindfulness}
                  onChange={(e) => setContentMix({...contentMix, mindfulness: e.target.checked})}
                />
                <span className="text-sm">ðŸ§˜ Mindfulness ({mindfulnessPosts.length})</span>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.motivational}
                  onChange={(e) => setContentMix({...contentMix, motivational: e.target.checked})}
                />
                <span className="text-sm">âœ¨ Motivational</span>
              </div>
              <div className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={contentMix.educational}
                  onChange={(e) => setContentMix({...contentMix, educational: e.target.checked})}
                />
                <span className="text-sm">ðŸ“š Educational</span>
              </div>
            </div>
            <p className="text-xs text-green-600 mt-2">
              These checkboxes control which content types are available for "Random Mix" days.
            </p>
          </div>

          <button
            onClick={generateWeeklyContent}
            disabled={isGenerating}
            className={`w-full px-6 py-3 text-white rounded-lg font-medium ${
              isGenerating ? 'bg-gray-400' : 'bg-blue-500 hover:bg-blue-600'
            }`}
          >
            {isGenerating ? 'Generating...' : 'Generate Weekly Content'}
          </button>
        </div>
      )}
    </div>
  );
}

export default SocialMediaAgent;
