import React, { useState, useRef, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Calendar, Upload, Copy, Share, Target, FileText, ChefHat, Dumbbell, Camera, Lightbulb, X, Eye, Edit2, Check } from 'lucide-react';

function SocialMediaAgent() {
  // State initialization
  const [recipes, setRecipes] = useState([]);
  const [workouts, setWorkouts] = useState([]);
  const [realEstateTips, setRealEstateTips] = useState([]);
  const [mindfulnessPosts, setMindfulnessPosts] = useState([]);
  const [contentCalendar, setContentCalendar] = useState([]);
  const [activeTab, setActiveTab] = useState('dashboard');

  // Form states
  const [newRecipe, setNewRecipe] = useState({ title: '', ingredients: '', instructions: '', tags: '', url: '' });
  const [newWorkout, setNewWorkout] = useState({ title: '', exercises: '', duration: '', difficulty: '', tags: '', url: '' });
  const [newRealEstateTip, setNewRealEstateTip] = useState({ title: '', content: '', category: '', tags: '', url: '' });
  const [newMindfulness, setNewMindfulness] = useState({ title: '', content: '', practice: '', tags: '', url: '' });

  // Preview and editing states
  const [activeImageModal, setActiveImageModal] = useState(null);
  const [previewRecipe, setPreviewRecipe] = useState(null);
  const [previewWorkout, setPreviewWorkout] = useState(null);
  const [editingRecipe, setEditingRecipe] = useState(null);
  const [editingWorkout, setEditingWorkout] = useState(null);

  // AI settings
  const [apiKey, setApiKey] = useState('');
  const [llmProvider, setLlmProvider] = useState('openai');
  const [isGenerating, setIsGenerating] = useState(false);
  const [showApiSettings, setShowApiSettings] = useState(false);
  const [contentMix, setContentMix] = useState({
    recipes: true,
    workouts: true,
    realEstate: true,
    mindfulness: true,
    motivational: true,
    educational: true
  });
  const [weeklySchedule, setWeeklySchedule] = useState({
    monday: 'motivational',
    tuesday: 'workout',
    wednesday: 'mindfulness',
    thursday: 'realEstate',
    friday: 'educational',
    saturday: 'recipe',
    sunday: 'random'
  });

  // Refs
  const recipeFileInputRef = useRef(null);
  const workoutFileInputRef = useRef(null);

  const platforms = {
    instagram: { name: 'Instagram', color: 'bg-pink-500', icon: 'ðŸ“¸' },
    linkedin: { name: 'LinkedIn', color: 'bg-blue-600', icon: 'ðŸ’¼' },
    facebook: { name: 'Facebook', color: 'bg-blue-500', icon: 'ðŸ‘¥' }
  };

  const contentTypes = {
    recipe: { name: 'Recipe', icon: ChefHat, color: 'text-orange-500' },
    workout: { name: 'Workout', icon: Dumbbell, color: 'text-green-500' },
    realEstate: { name: 'Real Estate', icon: FileText, color: 'text-blue-500' },
    mindfulness: { name: 'Mindfulness', icon: Target, color: 'text-purple-500' },
    motivational: { name: 'Motivational', icon: Target, color: 'text-pink-500' },
    educational: { name: 'Educational', icon: FileText, color: 'text-indigo-500' }
  };

  // Helper functions
  const addRecipe = () => {
    if (newRecipe.title.trim()) {
      setRecipes([...recipes, { ...newRecipe, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewRecipe({ title: '', ingredients: '', instructions: '', tags: '', url: '' });
    }
  };

  const addWorkout = () => {
    if (newWorkout.title.trim()) {
      setWorkouts([...workouts, { ...newWorkout, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewWorkout({ title: '', exercises: '', duration: '', difficulty: '', tags: '', url: '' });
    }
  };

  const addRealEstateTip = () => {
    if (newRealEstateTip.title.trim()) {
      setRealEstateTips([...realEstateTips, { ...newRealEstateTip, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewRealEstateTip({ title: '', content: '', category: '', tags: '', url: '' });
    }
  };

  const addMindfulness = () => {
    if (newMindfulness.title.trim()) {
      setMindfulnessPosts([...mindfulnessPosts, { ...newMindfulness, id: Date.now(), createdAt: new Date().toISOString() }]);
      setNewMindfulness({ title: '', content: '', practice: '', tags: '', url: '' });
    }
  };

  const deleteRecipe = (id) => {
    setRecipes(recipes.filter(r => r.id !== id));
    if (previewRecipe?.id === id) setPreviewRecipe(null);
  };

  const deleteWorkout = (id) => {
    setWorkouts(workouts.filter(w => w.id !== id));
    if (previewWorkout?.id === id) setPreviewWorkout(null);
  };

  const deleteRealEstate = (id) => {
    setRealEstateTips(realEstateTips.filter(t => t.id !== id));
  };

  const deleteMindfulness = (id) => {
    setMindfulnessPosts(mindfulnessPosts.filter(m => m.id !== id));
  };

  const generatePostVariations = (content, type) => {
    const variations = {
      instagram: {
        recipe: `ðŸ½ï¸ ${content.title}\n\nâœ¨ Perfect for your weekly meal prep!\n\n#healthyeating #mealprep #recipe`,
        workout: `ðŸ’ª ${content.title}\n\nðŸ”¥ ${content.duration || 'Get moving'} of pure energy!\n\n#fitness #workout #motivation`,
        realEstate: `ðŸ¡ ${content.title}\n\nâœ¨ Your dream home awaits!\n\n#realestate #homebuying #property`,
        mindfulness: `ðŸ§˜ ${content.title}\n\nâœ¨ Find your peace today\n\n#mindfulness #meditation #wellness`,
        motivational: `âœ¨ Monday Motivation âœ¨\n\n"Your only limit is your mind"\n\n#motivation #mindset`,
        educational: `ðŸ“š Did you know?\n\n${content.title}\n\n#facts #education #wellness`
      },
      linkedin: {
        recipe: `Nutrition and Performance: ${content.title}\n\n#nutrition #wellness #productivity`,
        workout: `Peak Performance Through Fitness: ${content.title}\n\n#leadership #wellness #fitness`,
        realEstate: `Market Insight: ${content.title}\n\nNavigating today's real estate landscape.\n\n#realestate #investing #markettrends`,
        mindfulness: `Leadership Through Mindfulness: ${content.title}\n\nMental clarity drives success.\n\n#leadership #mindfulness #productivity`,
        motivational: `Leadership Insight: ${content.title}\n\n#leadership #mindset #success`,
        educational: `Industry Insight: ${content.title}\n\n#industry #insights #knowledge`
      },
      facebook: {
        recipe: `ðŸ¥— Family Recipe: ${content.title}\n\n#familytime #healthycooking #recipes`,
        workout: `ðŸ’ª Community Fitness: ${content.title}\n\n#communityfitness #workout #motivation`,
        realEstate: `ðŸ¡ Home Sweet Home: ${content.title}\n\nLet me help you find your perfect place!\n\n#realestate #homeowner`,
        mindfulness: `ðŸŒ¸ Wellness Wednesday: ${content.title}\n\nTake a moment for yourself.\n\n#mindfulness #selfcare`,
        motivational: `âœ¨ ${content.title}\n\n#motivation #community #support`,
        educational: `ðŸ’¡ ${content.title}\n\n#wellness #health #education`
      }
    };

    return variations;
  };

  const generateWeeklyContent = () => {
    setIsGenerating(true);
    const weeklyPlan = [];
    const today = new Date();
    const dayNames = ['sunday', 'monday', 'tuesday', 'wednesday', 'thursday', 'friday', 'saturday'];

    const availableContent = {
      recipe: recipes,
      workout: workouts,
      realEstate: realEstateTips,
      mindfulness: mindfulnessPosts
    };

    for (let i = 0; i < 7; i++) {
      const date = new Date(today);
      date.setDate(today.getDate() + i);
      const dayName = dayNames[date.getDay()];

      let contentType = weeklySchedule[dayName];
      let content;

      // Handle 'random' selection
      if (contentType === 'random') {
        const enabledTypes = Object.keys(contentMix).filter(key => 
          contentMix[key] && (availableContent[key]?.length > 0 || ['motivational', 'educational'].includes(key))
        );
        if (enabledTypes.length > 0) {
          contentType = enabledTypes[Math.floor(Math.random() * enabledTypes.length)];
        } else {
          contentType = 'motivational';
        }
      }

      // Get content based on type
      if (contentType === 'recipe' && availableContent.recipe.length > 0) {
        content = availableContent.recipe[Math.floor(Math.random() * availableContent.recipe.length)];
      } else if (contentType === 'workout' && availableContent.workout.length > 0) {
        content = availableContent.workout[Math.floor(Math.random() * availableContent.workout.length)];
      } else if (contentType === 'realEstate' && availableContent.realEstate.length > 0) {
        content = availableContent.realEstate[Math.floor(Math.random() * availableContent.realEstate.length)];
      } else if (contentType === 'mindfulness' && availableContent.mindfulness.length > 0) {
        content = availableContent.mindfulness[Math.floor(Math.random() * availableContent.mindfulness.length)];
      } else if (contentType === 'motivational') {
        content = { title: 'Daily Motivation' };
      } else if (contentType === 'educational') {
        content = { title: 'Wellness Tips' };
      } else {
        // Fallback if selected type has no content
        content = { title: 'Daily Inspiration' };
        contentType = 'motivational';
      }

      const fallback = generatePostVariations(content, contentType);
      const postData = {
        id: Date.now() + i,
        date: date.toISOString().split('T')[0],
        dayName: dayName,
        contentType,
        content,
        platforms: ['instagram', 'linkedin', 'facebook'],
        status: 'draft',
        variations: {
          instagram: fallback.instagram[contentType] || fallback.instagram.motivational,
          linkedin: fallback.linkedin[contentType] || fallback.linkedin.motivational,
          facebook: fallback.facebook[contentType] || fallback.facebook.motivational
        }
      };

      weeklyPlan.push(postData);
    }

